import axios from 'axios';
import { createUrl, log } from '../utils/utils';

export async function registerUser(
  firstName,
  lastName,
  address,
  phone,
  email,
  password,
  userRoles
) {
  const url = createUrl('/users');
  const body = {
    firstName,
    lastName,
    address,
    phone,
    email,
    password,
    userRoles
  };

  try {
    // Make a POST request to register the user
    const response = await axios.post(url, body);
    log(response.data);

    // Assuming the registration was successful, make a GET request to fetch user data by email
    try {
      const getUserUrl = createUrl(`/users?email=${encodeURIComponent(email)}`);
      const getUserResponse = await axios.get(getUserUrl);
      log(getUserResponse.data);
      return getUserResponse.data;
    } catch (getUserEx) {
      log('Error fetching user data by email:', getUserEx);
      return null;
    }
  } catch (ex) {
    log('Error registering user:', ex);
    return null;
  }
}
