import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { createUrl, log } from '../utils/utils';
const Salesperson = () => {
  const [carBookings, setCarBookings] = useState([]);
  const [finances, setFinances] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [insurances, setInsurances] = useState([]);
  const [testDriveBookings, setTestDriveBookings] = useState([]);
  const [carServices, setCarServices] = useState([]);
  const token = localStorage.getItem('token');


  useEffect(() => {
    fetchCarBookings();
    fetchFinances();
    fetchCustomers();
    fetchInsurances();
    fetchTestDriveBookings();
    fetchCarServices();
  }, []);

  // Fetch functions (similar to previous code)

  const fetchCarBookings =  () => {
    const url = createUrl('/bookings')
        axios.get(url)
            .then(function (response) {
                setCarBookings(response.data);

                log(response);

            })
            .catch(function (error) {

                log(error);
            })
            .finally(function () {

            });
  };


const fetchFinances =  () => {
    const url = createUrl('/finance')
        axios.get(url)
            .then(function (response) {
                setFinances(response.data);

                log(response);

            })
            .catch(function (error) {

                log(error);
            })
            .finally(function () {

            });
  };

  const fetchCustomers =  () => {
    const url = createUrl('/finance')
        axios.get(url)
            .then(function (response) {
                setCustomers(response.data);

                log(response);

            })
            .catch(function (error) {

                log(error);
            })
            .finally(function () {

            });
  };


  const fetchInsurances = async () => {
    try {
      const response = await axios.get('http://localhost:6060/insurances');
      setInsurances(response.data);
    } catch (error) {
      console.error('Error fetching insurances:', error);
    }
  };

  const fetchTestDriveBookings = async () => {
    try {
      const response = await axios.get('http://localhost:6060/bookings/testdrive');
      setTestDriveBookings(response.data);
    } catch (error) {
      console.error('Error fetching test drive bookings:', error);
    }
  };

  const fetchCarServices = async () => {
    try {
      const response = await axios.get('http://localhost:6060/car-services');
      setCarServices(response.data);
    } catch (error) {
      console.error('Error fetching car services:', error);
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    // Redirect or handle logout logic as needed

  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Salesperson Dashboard</h1>

      {/* Car Bookings */}
      <div className="card mb-3">
        <div className="card-body">
          <h5 className="card-title">Car Bookings</h5>
          <table className="table">
            <thead>
              <tr>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Mobile</th>
                <th>Car Brand</th>
                <th>Car Model</th>
              </tr>
            </thead>
            <tbody>
              {carBookings.map((booking) => (
                <tr key={booking.id}>
                  <td>{booking.user.firstName}</td>
                  <td>{booking.user.lastName}</td>
                  <td>{booking.user.phone}</td>
                  <td>{booking.car.brandName}</td>
                  <td>{booking.car.modelName}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Finances */}
      <div className="card mb-3">
        <div className="card-body">
          <h5 className="card-title">Finances</h5>
          {finances.map((finance) => (
            <div key={finance.id}>
              <p>Customer: {finance.loanAmount}</p>
              <p>Amount: {finance.amount}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Customers */}
      <div className="card mb-3">
        <div className="card-body">
          <h5 className="card-title">Customers</h5>
          {customers.map((customer) => (
            <div key={customer.id}>
              <p>Name: {customer.name}</p>
              <p>Email: {customer.email}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Insurances */}
      <div className="card mb-3">
        <div className="card-body">
          <h5 className="card-title">Insurances</h5>
          {insurances.map((insurance) => (
            <div key={insurance.id}>
              <p>Customer: {insurance.customerName}</p>
              <p>Policy Number: {insurance.policyNumber}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Test Drive Bookings */}
      <div className="card mb-3">
        <div className="card-body">
          <h5 className="card-title">Test Drive Bookings</h5>
          {testDriveBookings.map((booking) => (
            <div key={booking.id}>
              <p>Customer: {booking.customerName}</p>
              <p>Car: {booking.carName}</p>
              <p>Date: {booking.date}</p>
              {/* Display other test drive booking details */}
            </div>
          ))}
        </div>
      </div>

      {/* Car Services */}
      <div className="card mb-3">
        <div className="card-body">
          <h5 className="card-title">Car Services</h5>
          {carServices.map((service) => (
            <div key={service.id}>
              <p>Service Type: {service.serviceType}</p>
              <p>Car: {service.carName}</p>
              {/* Display other car service details */}
            </div>
          ))}
        </div>
      </div>

      <button className="btn btn-danger" onClick={logout}>
        Logout
      </button>
    </div>
  );
};

export default Salesperson;
