import { Container, Row } from "reactstrap";
import Helmet from "../components/Helmet/Helmet";
import CommonSection from "../components/UI/CommonSection";
import "../styles/about.css";
import axios from "axios";
import { createUrl, log } from '../utils/utils';
import { toast } from 'react-toastify';
import { useEffect, useState } from "react";

const Admin = () => {

    const [cars, setCars] = useState([]);
    const [car, setCar] = useState({ id: 0, brandName: "", modelName: "", price: 0, fuelType: "" });

    useEffect(() => {
        loadCar();
    }, []);

    const onTextChange=(args)=> {
        var copyOfCar = {...car};
        copyOfCar[args.target.name] = args.target.value;
        setCar(copyOfCar);
    }


    const loadCar = () => {
        const url = createUrl('/cars')
        axios.get(url)
            .then(function (response) {
                setCars(response.data);

                log(response);

            })
            .catch(function (error) {

                log(error);
            })
            .finally(function () {

            });
    }

    const deleteCar = (id) => {
        const url = createUrl(`/cars/${id}`)
        axios.delete(url)
            .then(res => {
                log(res);
                toast.error('Car Deleted SucessFully!! id = ' + id)
                log(res.data);
                loadCar();

                const posts = this.state.posts.filter(item => item.id !== id);
                this.setState({ posts });
            })
    }

    const editCar = (id) => {

            cars.map((forEdit) => {
                debugger
                if(forEdit.id == id)
                {;
                    var cpy = {...forEdit}

                    setCar(cpy);
                    return;
                }

            })
        }

    return (
        <Helmet title="Admin">
            <CommonSection title="Admin" />
            <section className="about__page-section">
                <Container>
                    <Row>
                        <div className="col-md-10">

                            <table className="table" style={{ border: "1px solid black" }}>
                                <thead className="thead-dark">
                                    <tr>
                                        <th scope="col">ID</th>
                                        <th scope="col">Name</th>
                                        <th scope="col">Model</th>
                                        <th scope="col">Price</th>
                                        <th scope="col">fuelType</th>

                                    </tr>

                                </thead>
                                <tbody>
                                    {
                                        cars.map((data) => {
                                            return (
                                                <tr key={data.id}>
                                                    <td>{data.id}</td>
                                                    <td>{data.brandName}</td>
                                                    <td>{data.modelName}</td>
                                                    <td>{data.price}</td>
                                                    <td>{data.fuelType}</td>
                                                    <td>
                                                        <button className="btn btn-danger btn-sm" onClick={() => { deleteCar(data.id) }} >Delete</button>
                                                    </td>
                                                    <td>
                                                        <button className="btn btn-info btn-sm" onClick={() => { editCar(data.id) }}  >Edit</button>
                                                    </td>
                                                </tr>
                                            )
                                        })
                                    }
                                </tbody>
                            </table>
                        </div>



                        {/* -------------------------------------------------------------------------------------- */}

                        <div className='col-md-6'>
                            <br />
                            <div className='myTable'>
                                <div className="form-group">
                                    <input type="number" className="form-control"
                                        name="Eno" value={id} onChange={onTextChange}
                                        placeholder="Enter Eno" />
                                </div>
                                <div className="form-group">
                                    <input type="text" className="form-control"
                                        name="brandName" value={brandName} onChange={onTextChange}
                                        placeholder="Enter Ename" />
                                </div>
                                <div className="form-group">
                                    <input type="text" className="form-control"
                                        name="modelName" value={modelName} onChange={onTextChange}
                                        placeholder="Enter Eaddress" />
                                </div>
                                <div className="form-group">
                                    <input type="text" className="form-control"
                                        name="price" value={price} onChange={onTextChange}
                                        placeholder="Enter Eaddress" />
                                </div>
                                <div className="form-group">
                                    <input type="text" className="form-control"
                                        name="fuelType" value={fuelType} onChange={onTextChange}
                                        placeholder="Enter Eaddress" />
                                </div>
                                <button className="btn btn-primary" 
                                >
                                    Add Record
                                </button>
                                <button className="btn btn-primary" style={{ margin: "10px" }} 
                                >
                                    Update-Record
                                </button>
                            </div>
                        </div>

                    </Row>
                </Container>
            </section>
        </Helmet>
    )
}

export default Admin;
