import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { createUrl, log } from '../utils/utils';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';

const Admin = () => {
    const navigate = useNavigate();

    const [showAddCarForm, setShowAddCarForm] = useState(false);
    const [newCar, setNewCar] = useState({
        brandName: '',
        modelName: '',
        year: 0,
        price: 0,
        mileage: 0,
        fuelType: '',
        transmissionType: '',
        description: '',
        carSpec: {
            color: '',
            engine: '',
            horsepower: 0,
            dimensions: 0,
        },
    });

    useEffect(() => {
        fetchCarBookings();
        fetchUsers();
        fetchCars();
    }, []);

    const toggleAddCarForm = () => {
        setShowAddCarForm(!showAddCarForm);
    };

    const handleNewCarChange = (event) => {
        const { name, value } = event.target;
        setNewCar({
            ...newCar,
            [name]: value,
        });
    };

    const handleCarSpecChange = (event) => {
        const { name, value } = event.target;
        setNewCar({
            ...newCar,
            carSpec: {
                ...newCar.carSpec,
                [name]: value,
            },
        });
    };

    const addNewCar = () => {
        // Check if all required fields are filled
        if (
            newCar.brandName &&
            newCar.modelName &&
            newCar.year &&
            newCar.price &&
            newCar.mileage &&
            newCar.fuelType &&
            newCar.transmissionType &&
            newCar.description &&
            newCar.carSpec.color &&
            newCar.carSpec.engine &&
            newCar.carSpec.horsepower &&
            newCar.carSpec.dimensions
        ) {
            const url = createUrl('/cars');
            axios
                .post(url, newCar)
                .then((res) => {
                    toast.success('Car Added Successfully!');
                    fetchCars();
                    toggleAddCarForm();
                    setNewCar({
                        brandName: '',
                        modelName: '',
                        year: 0,
                        price: 0,
                        mileage: 0,
                        fuelType: '',
                        transmissionType: '',
                        description: '',
                        carSpec: {
                            color: '',
                            engine: '',
                            horsepower: 0,
                            dimensions: 0,
                        },
                    });
                })
                .catch((error) => {
                    log(error);
                });
        } else {
            toast.error('Please fill in all the required fields.');
        }
    };

    const logout = () => {
        navigate('/');
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center mb-4">Admin Dashboard</h1>

            {/* ... (car bookings table) */}

            {/* ... (users table) */}

            {/* ... (salesperson table) */}

            <div className="card mb-3">
                <div className="card-body">
                    <h5 className="card-title">Cars with Details</h5>
                    <table className="table">
                        {/* ... (table headers) */}
                        <tbody>
                            {showAddCarForm ? (
                                <tr>
                                    <td>
                                        <input
                                            type="text"
                                            name="brandName"
                                            value={newCar.brandName}
                                            onChange={handleNewCarChange}
                                            placeholder="Brand Name"
                                        />
                                    </td>
                                    <td>
                                        <input
                                            type="text"
                                            name="modelName"
                                            value={newCar.modelName}
                                            onChange={handleNewCarChange}
                                            placeholder="Model Name"
                                        />
                                    </td>
                                    {/* ... (other input fields) */}
                                    <td>
                                        <button
                                            className="btn btn-outline-primary btn-sm"
                                            onClick={addNewCar}
                                        >
                                            Add Car
                                        </button>
                                        <button
                                            className="btn btn-outline-secondary btn-sm ml-2"
                                            onClick={toggleAddCarForm}
                                        >
                                            Cancel
                                        </button>
                                    </td>
                                </tr>
                            ) : (
                                <tr>
                                    <td colSpan="7">
                                        <button
                                            className="btn btn-outline-secondary btn-sm"
                                            onClick={toggleAddCarForm}
                                        >
                                            Add Car
                                        </button>
                                    </td>
                                </tr>
                            )}
                            {/* ... (existing cars table rows) */}
                        </tbody>
                    </table>
                </div>
            </div>

            <button className="btn btn-outline-danger btn-sm" onClick={logout}>
                Logout
            </button>
        </div>
    );
};

export default Admin;
